package com.logicbuilding;

public class SwapTwoNumbersSol2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		int b=20;
		System.out.print("a= "+a);
		System.out.print("b= "+b);
		
		a=a+b;//a=30
		System.out.print("new value a= "+a);
		b=a-b;//b=10
		System.out.print("new value final b= "+b);
		a=a-b;//20
		System.out.print("new value final a= "+a);
		
		
		
		

	}

}
